import React from "react";
import "./Message.css";

const Message = ({ msg, chat }) => {
  console.log(msg, "msg");
  console.log(chat, "chat");
  const color = msg.from === chat.uid ? "#FF7597" : "#424242";
  const alignments = msg.from === chat.uid ? "flex-start" : "flex-end";
  const textColor = msg.from === chat.uid ? 'black' : 'white';
  console.log(color)
  return (
    <div className="message-wrapper" style={{ justifyContent: alignments , marginBottom: '10px'}}>
      <div className="message-image" style={{ background: color }}>
        {msg.media ? (
          <img
            src={msg.media}
            style={{
              width: "100px",
              height: "100px",
              borderRadius: "5px",
              objectFit: "cover",
            }}
          />
        ) : (
          <div className="message-text" style={{ color: textColor, borderRadius: "5px"}}>{msg.text}</div>
        )}
      </div>
    </div>
  );
};

export default Message;
