import React, { useState, useContext } from "react";
import "./Login.css";
import { useNavigate } from "react-router-dom";
import LinkButton from "../../components/LinkButton/LinkButton";
import { AuthContext } from "../../context/auth";

const Login = ({ dark }) => {
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);
  // console.log(user)

  //set data - DATA COLLECTION
  const [data, setData] = useState({
    email: "",
    password: "",
    error: null,
    loading: false,
  });

  //destructure
  const { email, password, error, loading } = data;

  //color scheme set
  const backgroundColor = dark ? "#121212" : "#F2EEEE";
  const primary = dark ? "white" : "black";
  return (
    <div className="login-wrapper" style={{ backgroundColor: backgroundColor }}>
      <h1 style={{ color: primary, fontSize: "40px" }}> Welcome back!! </h1>
      <form className="form">
        <input
          className="input-field"
          type="text"
          name="email"
          placeholder="Email"
        />
        <br />
        <br />
        <input
          className="input-field"
          type="password"
          name="password"
          placeholder="Password"
        />
        <br />
        <br />
        <div className="btn">
          <LinkButton
            path={"/userPage"}
            content={"Login"}
            primary={dark}
            small={true}
          />
        </div>
      </form>
    </div>
  );
};

export default Login;
