import React from "react";
import "./Message.css";

const Message = ({ msg, chat }) => {
  console.log(msg, "msg");
  console.log(chat, "chat");
  const alignment = msg.from === chat.uid ? "flex-start" : "flex-end";
  return (
    <div className="message-wrapper">
      <div className="message-image" style={{ justifyContent: alignment }}>
        {msg.media ? (
          <img
            src={msg.media}
            style={{
              width: "100px",
              height: "100px",
              borderRadius: "5px",
              objectFit: "cover",
            }}
          />
        ) : (
          <div className="message-text">{msg.text}</div>
        )}
      </div>
    </div>
  );
};

export default Message;
